import React, {useEffect, useState} from "react";
import Property from "./components/Property";

import axios from "axios";

const IntiniteScroll = (props) => {
    const {
        children,
        getNext,
        isLoading

    } = props;

    const handleScroll = () => {

        if (window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight){
            return;
        } 
        getNext();
    }

    useEffect( ()=> {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    })

    return (<div>
        <div>{children}</div>
        {isLoading ? <div>Loading</div> : null}
    </div>)

}

const PropertyList = (props) => {

    const [properties, setProperties] = useState([]);
    const [pageNumber, setPageNumber] = useState(1)
    const [retryCount, setRetryCount] = useState(0);
    const [isLoading, setIsLoading] = useState(true);

    const getProperties = () =>  {

        setIsLoading(true);

        return axios.get(`http://app-homevision-staging.herokuapp.com/api_project/houses?page=${pageNumber}&per_page=20`)
        .then(response => {
            setProperties( properties.concat(response.data.houses));
            setPageNumber(pageNumber+1);
            setIsLoading(false)
        })
        .catch( e => {
            console.error(e);
            setRetryCount( retryCount+1);
            setTimeout(getProperties, 2000);
        } )
    }

    useEffect( ()=> {
        if (properties.length === 0){
            getProperties();
        }  
    }, [properties])

    return (<IntiniteScroll
        getNext={getProperties}>
        {properties.map( property => (
            <Property {...property} key={property.id}/>
        )
            )}
    </IntiniteScroll>)
}

export default PropertyList;
